$(document).ready(function(){

                $("#alert-message").hide();
                $("#index-search-btn").click(function() {
                    var location = $("#location_name").val();
                    if(!location)
                    {
                        $("#alert-message").show();
                    }
                    else
                    {
                        $("#alert-message").hide();
                        window.open("home.html","_self");
                    }

                    event.preventDefault();
                });



});